window.addEventListener('DOMContentLoaded', function() {
    var elements = document.querySelectorAll('.crm-kanban-item-title');
    if (elements.length > 0) {
        elements.forEach(function(element) {
            element.innerText += ' test1';
        });
    }
    const pars = document.querySelector('#nik_totop-params').dataset.params;
    const correctPars = JSON.parse(pars)
    console.log(correctPars)


    var currentURL = window.location.href;
    var regex = /\/crm\/deal\/details\/\d+\//;

    if (regex.test(currentURL)) {
        var pagetitleElement = document.querySelector('.pagetitle.crm-pagetitle');

        if (pagetitleElement && !pagetitleElement.dataset.test1Added) {
            var newText = 'test1 ';
            pagetitleElement.innerText = newText + pagetitleElement.innerText;
            pagetitleElement.dataset.test1Added = true;
        }
    }
});